<?phpuse Webhd\Helpers\Arr;\defined( 'ABSPATH' ) || exit;global $post;$acf_fields = \get_fields( $post->ID ) ?? '';$acf_products_attrs = $acf_fields['acf_products_attrs'] ?? '';if ( $acf_products_attrs ) :?><div class="custom-short-description">    <?php foreach ( $acf_products_attrs as $acf_item ) : ?>    <div class="feature-list">        <div class="icon">            <?php echo $acf_item['re_thumb'] ?? ''; ?>            <?php echo $acf_item['re_svg'] ?? ''; ?>        </div>        <div class="desc">            <?php echo $acf_item['re_product_desc'] ?? ''; ?>        </div>    </div>    <?php endforeach; ?></div><?php endif;